package cn.moxhub.graduation.controller;import cn.moxhub.graduation.model.dto.CollectDTO;import cn.moxhub.graduation.model.dto.ResponseDTO;import cn.moxhub.graduation.model.sd.Img2ImgRequest;import cn.moxhub.graduation.model.sd.Text2ImgRequest;import cn.moxhub.graduation.service.CollectService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.*;import org.springframework.web.client.RestTemplate;import javax.annotation.Resource;/** * @ClassName :UserController * @Description : * @Author :Mox * @Date :2023/5/15 19:54 * @Version : v1.0 **/@Api(tags = "[Collect] 收藏管理接口")@CrossOrigin@RestController@RequestMapping("collect")public class CollectController {    @Resource    private CollectService collectService;    @GetMapping(value = "admin/getCollectList")    @ApiOperation("获取所有收藏列表")    public ResponseDTO getCollectList(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.getCollectList(request);        return res;    }    @GetMapping(value = "getCollectByUserId")    @ApiOperation("获取个人收藏列表")    public ResponseDTO getCollectByUserId(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.getCollectByUserId(request);        return res;    }    @GetMapping(value = "getCollectListByCollectId")    @ApiOperation("获取一条收藏")    public ResponseDTO getCollectListByCollectId(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.getCollectListByCollectId(request);        return res;    }    @PostMapping(value = "deleteCollectByCollectId")    @ApiOperation("删除一条收藏")    public ResponseDTO deleteCollectByCollectId(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.deleteCollectByCollectId(request);        return res;    }    @PostMapping(value = "deleteCollectByUserId")    @ApiOperation("删除一条收藏")    public ResponseDTO deleteCollectByUserId(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.deleteCollectByUserId(request);        return res;    }    @PostMapping(value = "createCollect")    @ApiOperation("删除一条收藏")    public ResponseDTO createCollect(@RequestBody CollectDTO request) {        ResponseDTO res = collectService.createCollect(request);        return res;    }}